public class Emotion {
	private String emotion;
	private String icon;
	private String desc;

	/**
	 * @return the emotion
	 */
	public String getEmotion() {
		return emotion;
	}

	/**
	 * @param emotion
	 *            the emotion to set
	 */
	public void setEmotion(String emotion) {
		this.emotion = emotion;
	}

	/**
	 * @return the icon
	 */
	public String getIcon() {
		return icon;
	}

	/**
	 * @param icon
	 *            the icon to set
	 */
	public void setIcon(String icon) {
		this.icon = icon;
	}

	/**
	 * @return the desc
	 */
	public String getDesc() {
		return desc;
	}

	/**
	 * @param desc
	 *            the desc to set
	 */
	public void setDesc(String desc) {
		this.desc = desc;
	}
}
